import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import cookieParser from "cookie-parser";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(cookieParser());
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import bcrypt from "bcrypt";
import jwt from "jsonwebtoken";
import multer from "multer";
import { storage } from "./storage";
import { sendWelcomeEmail } from "./emailService";
import { 
  insertUserSchema, 
  loginSchema, 
  insertAttendanceSchema,
  insertCalendarEventSchema,
  insertExamSchema,
  insertSyllabusSchema,
  insertChatMessageSchema
} from "@shared/schema";

const JWT_SECRET = process.env.JWT_SECRET || "your-secret-key";
const upload = multer({ dest: "uploads/" });

// Extended Request interface for TypeScript
interface AuthenticatedRequest extends Express.Request {
  user?: any;
  file?: any;
  cookies?: any;
}

// Middleware to verify JWT token
const authenticateToken = (req: AuthenticatedRequest, res: any, next: any) => {
  const token = req.cookies?.token;

  if (!token) {
    return res.status(401).json({ message: "Access token required" });
  }

  try {
    const decoded = jwt.verify(token, JWT_SECRET) as any;
    req.user = decoded;
    next();
  } catch (error) {
    return res.status(403).json({ message: "Invalid token" });
  }
};

export async function registerRoutes(app: Express): Promise<Server> {
  const httpServer = createServer(app);

  // WebSocket server for real-time chat
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });
  const connectedClients = new Map<string, WebSocket>();

  wss.on('connection', (ws: WebSocket, req) => {
    console.log('New WebSocket connection');

    ws.on('message', async (data) => {
      try {
        const message = JSON.parse(data.toString());

        if (message.type === 'join') {
          connectedClients.set(message.userId, ws);
        } else if (message.type === 'chat') {
          // Save message to storage
          const chatMessage = await storage.createChatMessage({
            userId: message.userId,
            message: message.text,
            room: message.room || 'general'
          });

          // Broadcast to all connected clients
          const broadcastData = JSON.stringify({
            type: 'chat',
            message: chatMessage
          });

          wss.clients.forEach((client) => {
            if (client.readyState === WebSocket.OPEN) {
              client.send(broadcastData);
            }
          });
        }
      } catch (error) {
        console.error('WebSocket message error:', error);
      }
    });

    ws.on('close', () => {
      // Remove client from connected clients
      for (const [userId, client] of connectedClients) {
        if (client === ws) {
          connectedClients.delete(userId);
          break;
        }
      }
    });
  });

  // Auth routes
  app.post("/api/auth/register", upload.single('idPhoto'), async (req, res) => {
    try {
      const userData = insertUserSchema.parse(req.body);

      // Check if user already exists
      const existingUser = await storage.getUserByRollNo(userData.collegeRollNo);
      if (existingUser) {
        return res.status(400).json({ message: "User with this roll number already exists" });
      }

      // Hash password
      const hashedPassword = await bcrypt.hash(userData.password, 10);

      // Create user
      const user = await storage.createUser({
        ...userData,
        password: hashedPassword,
        idPhotoUrl: req.file?.path || null,
      });

      // Remove password from response
      const { password, ...userResponse } = user;

      // Send welcome email (don't block response if email fails)
      sendWelcomeEmail({
        studentName: user.fullName,
        studentEmail: user.studentEmail,
        collegeRollNo: user.collegeRollNo,
        parentEmail: user.parentEmail,
      }).catch(error => {
        console.error('Failed to send welcome email:', error);
      });

      res.status(201).json({ user: userResponse });
    } catch (error) {
      res.status(400).json({ message: "Registration failed", error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.post("/api/auth/login", async (req, res) => {
    try {
      const { collegeRollNo, password } = loginSchema.parse(req.body);

      const user = await storage.getUserByRollNo(collegeRollNo);
      if (!user) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      const isValidPassword = await bcrypt.compare(password, user.password);
      if (!isValidPassword) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      // Generate JWT token
      const token = jwt.sign(
        { userId: user.id, rollNo: user.collegeRollNo },
        JWT_SECRET,
        { expiresIn: "24h" }
      );

      // Set httpOnly cookie
      res.cookie("token", token, {
        httpOnly: true,
        secure: process.env.NODE_ENV === "production",
        sameSite: "strict",
        maxAge: 24 * 60 * 60 * 1000, // 24 hours
      });

      const { password: _, ...userResponse } = user;
      res.json({ user: userResponse });
    } catch (error) {
      res.status(400).json({ message: "Login failed", error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.post("/api/auth/logout", (req, res) => {
    res.clearCookie("token");
    res.json({ message: "Logged out successfully" });
  });

  app.get("/api/auth/me", authenticateToken, async (req, res) => {
    try {
      const user = await storage.getUser(req.user.userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      const { password, ...userResponse } = user;
      res.json({ user: userResponse });
    } catch (error) {
      res.status(500).json({ message: "Failed to get user", error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  // Attendance routes
  app.get("/api/attendance", authenticateToken, async (req, res) => {
    try {
      const { startDate, endDate } = req.query;
      const records = await storage.getAttendanceRecords(
        req.user.userId,
        startDate ? new Date(startDate as string) : undefined,
        endDate ? new Date(endDate as string) : undefined
      );
      res.json({ records });
    } catch (error) {
      res.status(500).json({ message: "Failed to get attendance records", error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.post("/api/attendance", authenticateToken, async (req, res) => {
    try {
      const attendanceData = insertAttendanceSchema.parse({
        ...req.body,
        userId: req.user.userId,
        date: new Date(req.body.date || Date.now()),
      });

      const record = await storage.createAttendanceRecord(attendanceData);
      res.status(201).json({ record });
    } catch (error) {
      res.status(400).json({ message: "Failed to create attendance record", error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.get("/api/attendance/stats", authenticateToken, async (req, res) => {
    try {
      const stats = await storage.getAttendanceStats(req.user.userId);
      res.json({ stats });
    } catch (error) {
      res.status(500).json({ message: "Failed to get attendance stats", error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  // Calendar routes
  app.get("/api/calendar", authenticateToken, async (req, res) => {
    try {
      const { startDate, endDate } = req.query;
      const events = await storage.getCalendarEvents(
        startDate ? new Date(startDate as string) : undefined,
        endDate ? new Date(endDate as string) : undefined
      );
      res.json({ events });
    } catch (error) {
      res.status(500).json({ message: "Failed to get calendar events", error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.post("/api/calendar", authenticateToken, async (req, res) => {
    try {
      const eventData = insertCalendarEventSchema.parse({
        ...req.body,
        createdBy: req.user.userId,
        date: new Date(req.body.date),
        endDate: req.body.endDate ? new Date(req.body.endDate) : undefined,
      });

      const event = await storage.createCalendarEvent(eventData);
      res.status(201).json({ event });
    } catch (error) {
      res.status(400).json({ message: "Failed to create calendar event", error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  // Exam routes
  app.get("/api/exams", authenticateToken, async (req, res) => {
    try {
      const exams = await storage.getExamSchedule();
      res.json({ exams });
    } catch (error) {
      res.status(500).json({ message: "Failed to get exam schedule", error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.post("/api/exams", authenticateToken, async (req, res) => {
    try {
      const examData = insertExamSchema.parse({
        ...req.body,
        date: new Date(req.body.date),
      });

      const exam = await storage.createExam(examData);
      res.status(201).json({ exam });
    } catch (error) {
      res.status(400).json({ message: "Failed to create exam", error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  // Syllabus routes
  app.get("/api/syllabus", authenticateToken, async (req, res) => {
    try {
      const syllabus = await storage.getSyllabus();
      res.json({ syllabus });
    } catch (error) {
      res.status(500).json({ message: "Failed to get syllabus", error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  app.post("/api/syllabus", authenticateToken, async (req, res) => {
    try {
      const syllabusData = insertSyllabusSchema.parse({
        ...req.body,
        dueDate: req.body.dueDate ? new Date(req.body.dueDate) : undefined,
      });

      const item = await storage.createSyllabusItem(syllabusData);
      res.status(201).json({ item });
    } catch (error) {
      res.status(400).json({ message: "Failed to create syllabus item", error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  // Chat routes
  app.get("/api/chat/messages", authenticateToken, async (req, res) => {
    try {
      const { room = 'general', limit = 50 } = req.query;
      const messages = await storage.getChatMessages(room as string, parseInt(limit as string));
      res.json({ messages });
    } catch (error) {
      res.status(500).json({ message: "Failed to get chat messages", error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  // Face descriptor update route
  app.post("/api/user/face-descriptor", authenticateToken, async (req, res) => {
    try {
      const { faceDescriptor } = req.body;

      const updatedUser = await storage.updateUser(req.user.userId, {
        faceDescriptor,
      });

      if (!updatedUser) {
        return res.status(404).json({ message: "User not found" });
      }

      res.json({ message: "Face descriptor updated successfully" });
    } catch (error) {
      res.status(400).json({ message: "Failed to update face descriptor", error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  // Webhook for external attendance marking
  app.post("/api/webhooks/attendance", async (req, res) => {
    try {
      const { collegeRollNo, status, subject, location } = req.body;

      const user = await storage.getUserByRollNo(collegeRollNo);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      const record = await storage.createAttendanceRecord({
        userId: user.id,
        date: new Date(),
        status,
        subject,
        location,
        method: 'webhook',
        verified: true,
      });

      res.status(201).json({ record });
    } catch (error) {
      res.status(400).json({ message: "Failed to create attendance record", error: error instanceof Error ? error.message : 'Unknown error' });
    }
  });

  return httpServer;
}

server/storage.ts.content:
import { 
  type User, 
  type InsertUser, 
  type AttendanceRecord, 
  type InsertAttendance,
  type CalendarEvent,
  type InsertCalendarEvent,
  type ExamScheduleItem,
  type InsertExam,
  type SyllabusItem,
  type InsertSyllabus,
  type ChatMessage,
  type InsertChatMessage,
  users,
  attendanceRecords,
  calendarEvents,
  examSchedule,
  syllabus,
  chatMessages
} from "@shared/schema";
import { randomUUID } from "crypto";
import { db } from "./db";
import { eq, desc, and, gte, lte } from "drizzle-orm";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByRollNo(rollNo: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, updates: Partial<User>): Promise<User | undefined>;

  // Attendance operations
  getAttendanceRecords(userId: string, startDate?: Date, endDate?: Date): Promise<AttendanceRecord[]>;
  createAttendanceRecord(record: InsertAttendance): Promise<AttendanceRecord>;
  getAttendanceStats(userId: string): Promise<{
    totalPresent: number;
    totalAbsent: number;
    totalLeave: number;
    percentage: number;
  }>;

  // Calendar operations
  getCalendarEvents(startDate?: Date, endDate?: Date): Promise<CalendarEvent[]>;
  createCalendarEvent(event: InsertCalendarEvent): Promise<CalendarEvent>;
  updateCalendarEvent(id: string, updates: Partial<CalendarEvent>): Promise<CalendarEvent | undefined>;
  deleteCalendarEvent(id: string): Promise<boolean>;

  // Exam operations
  getExamSchedule(): Promise<ExamScheduleItem[]>;
  createExam(exam: InsertExam): Promise<ExamScheduleItem>;
  updateExam(id: string, updates: Partial<ExamScheduleItem>): Promise<ExamScheduleItem | undefined>;
  deleteExam(id: string): Promise<boolean>;

  // Syllabus operations
  getSyllabus(): Promise<SyllabusItem[]>;
  createSyllabusItem(item: InsertSyllabus): Promise<SyllabusItem>;
  updateSyllabusItem(id: string, updates: Partial<SyllabusItem>): Promise<SyllabusItem | undefined>;
  deleteSyllabusItem(id: string): Promise<boolean>;

  // Chat operations
  getChatMessages(room: string, limit?: number): Promise<ChatMessage[]>;
  createChatMessage(message: InsertChatMessage): Promise<ChatMessage>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User> = new Map();
  private attendanceRecords: Map<string, AttendanceRecord> = new Map();
  private calendarEvents: Map<string, CalendarEvent> = new Map();
  private examSchedule: Map<string, ExamScheduleItem> = new Map();
  private syllabus: Map<string, SyllabusItem> = new Map();
  private chatMessages: Map<string, ChatMessage> = new Map();

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByRollNo(rollNo: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.collegeRollNo === rollNo);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.studentEmail === email);
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = {
      ...insertUser,
      id,
      isActive: true,
      createdAt: new Date(),
      idPhotoUrl: insertUser.idPhotoUrl ?? null,
      faceDescriptor: insertUser.faceDescriptor ?? null,
    };
    this.users.set(id, user);
    return user;
  }

  async updateUser(id: string, updates: Partial<User>): Promise<User | undefined> {
    const user = this.users.get(id);
    if (!user) return undefined;

    const updatedUser = { ...user, ...updates };
    this.users.set(id, updatedUser);
    return updatedUser;
  }

  async getAttendanceRecords(userId: string, startDate?: Date, endDate?: Date): Promise<AttendanceRecord[]> {
    return Array.from(this.attendanceRecords.values())
      .filter(record => record.userId === userId)
      .filter(record => {
        if (startDate && record.date < startDate) return false;
        if (endDate && record.date > endDate) return false;
        return true;
      })
      .sort((a, b) => b.date.getTime() - a.date.getTime());
  }

  async createAttendanceRecord(insertRecord: InsertAttendance): Promise<AttendanceRecord> {
    const id = randomUUID();
    const record: AttendanceRecord = {
      ...insertRecord,
      id,
      createdAt: new Date(),
      subject: insertRecord.subject ?? null,
      method: insertRecord.method ?? null,
      location: insertRecord.location ?? null,
      reason: insertRecord.reason ?? null,
      verified: insertRecord.verified ?? null,
      latitude: insertRecord.latitude ?? null,
      longitude: insertRecord.longitude ?? null,
    };
    this.attendanceRecords.set(id, record);
    return record;
  }

  async getAttendanceStats(userId: string): Promise<{
    totalPresent: number;
    totalAbsent: number;
    totalLeave: number;
    percentage: number;
  }> {
    const records = await this.getAttendanceRecords(userId);
    const totalPresent = records.filter(r => r.status === 'present').length;
    const totalAbsent = records.filter(r => r.status === 'absent').length;
    const totalLeave = records.filter(r => r.status === 'leave').length;
    const total = records.length;

    return {
      totalPresent,
      totalAbsent,
      totalLeave,
      percentage: total > 0 ? Math.round((totalPresent / total) * 100) : 0,
    };
  }

  async getCalendarEvents(startDate?: Date, endDate?: Date): Promise<CalendarEvent[]> {
    return Array.from(this.calendarEvents.values())
      .filter(event => {
        if (startDate && event.date < startDate) return false;
        if (endDate && event.date > endDate) return false;
        return true;
      })
      .sort((a, b) => a.date.getTime() - b.date.getTime());
  }

  async createCalendarEvent(insertEvent: InsertCalendarEvent): Promise<CalendarEvent> {
    const id = randomUUID();
    const event: CalendarEvent = {
      ...insertEvent,
      id,
      createdAt: new Date(),
      description: insertEvent.description ?? null,
      endDate: insertEvent.endDate ?? null,
      createdBy: insertEvent.createdBy ?? null,
    };
    this.calendarEvents.set(id, event);
    return event;
  }

  async updateCalendarEvent(id: string, updates: Partial<CalendarEvent>): Promise<CalendarEvent | undefined> {
    const event = this.calendarEvents.get(id);
    if (!event) return undefined;

    const updatedEvent = { ...event, ...updates };
    this.calendarEvents.set(id, updatedEvent);
    return updatedEvent;
  }

  async deleteCalendarEvent(id: string): Promise<boolean> {
    return this.calendarEvents.delete(id);
  }

  async getExamSchedule(): Promise<ExamScheduleItem[]> {
    return Array.from(this.examSchedule.values())
      .sort((a, b) => a.date.getTime() - b.date.getTime());
  }

  async createExam(insertExam: InsertExam): Promise<ExamScheduleItem> {
    const id = randomUUID();
    const exam: ExamScheduleItem = {
      ...insertExam,
      id,
      createdAt: new Date(),
      instructions: insertExam.instructions ?? null,
    };
    this.examSchedule.set(id, exam);
    return exam;
  }

  async updateExam(id: string, updates: Partial<ExamScheduleItem>): Promise<ExamScheduleItem | undefined> {
    const exam = this.examSchedule.get(id);
    if (!exam) return undefined;

    const updatedExam = { ...exam, ...updates };
    this.examSchedule.set(id, updatedExam);
    return updatedExam;
  }

  async deleteExam(id: string): Promise<boolean> {
    return this.examSchedule.delete(id);
  }

  async getSyllabus(): Promise<SyllabusItem[]> {
    return Array.from(this.syllabus.values())
      .sort((a, b) => {
        if (a.dueDate && b.dueDate) {
          return a.dueDate.getTime() - b.dueDate.getTime();
        }
        return a.subject.localeCompare(b.subject);
      });
  }

  async createSyllabusItem(insertItem: InsertSyllabus): Promise<SyllabusItem> {
    const id = randomUUID();
    const item: SyllabusItem = {
      ...insertItem,
      id,
      createdAt: new Date(),
      description: insertItem.description ?? null,
      dueDate: insertItem.dueDate ?? null,
      completed: insertItem.completed ?? null,
    };
    this.syllabus.set(id, item);
    return item;
  }

  async updateSyllabusItem(id: string, updates: Partial<SyllabusItem>): Promise<SyllabusItem | undefined> {
    const item = this.syllabus.get(id);
    if (!item) return undefined;

    const updatedItem = { ...item, ...updates };
    this.syllabus.set(id, updatedItem);
    return updatedItem;
  }

  async deleteSyllabusItem(id: string): Promise<boolean> {
    return this.syllabus.delete(id);
  }

  async getChatMessages(room: string, limit: number = 50): Promise<ChatMessage[]> {
    return Array.from(this.chatMessages.values())
      .filter(message => message.room === room)
      .sort((a, b) => (b.createdAt?.getTime() ?? 0) - (a.createdAt?.getTime() ?? 0))
      .slice(0, limit)
      .reverse();
  }

  async createChatMessage(insertMessage: InsertChatMessage): Promise<ChatMessage> {
    const id = randomUUID();
    const message: ChatMessage = {
      ...insertMessage,
      id,
      createdAt: new Date(),
      room: insertMessage.room ?? null,
    };
    this.chatMessages.set(id, message);
    return message;
  }
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByRollNo(rollNo: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.collegeRollNo, rollNo));
    return user || undefined;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.studentEmail, email));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db.insert(users).values(insertUser).returning();
    return user;
  }

  async updateUser(id: string, updates: Partial<User>): Promise<User | undefined> {
    const [user] = await db.update(users).set(updates).where(eq(users.id, id)).returning();
    return user || undefined;
  }

  async getAttendanceRecords(userId: string, startDate?: Date, endDate?: Date): Promise<AttendanceRecord[]> {
    const conditions = [eq(attendanceRecords.userId, userId)];

    if (startDate) {
      conditions.push(gte(attendanceRecords.date, startDate));
    }
    if (endDate) {
      conditions.push(lte(attendanceRecords.date, endDate));
    }

    return await db.select().from(attendanceRecords)
      .where(and(...conditions))
      .orderBy(desc(attendanceRecords.date));
  }

  async createAttendanceRecord(insertRecord: InsertAttendance): Promise<AttendanceRecord> {
    const [record] = await db.insert(attendanceRecords).values(insertRecord).returning();
    return record;
  }

  async getAttendanceStats(userId: string): Promise<{
    totalPresent: number;
    totalAbsent: number;
    totalLeave: number;
    percentage: number;
  }> {
    const records = await this.getAttendanceRecords(userId);
    const totalPresent = records.filter(r => r.status === 'present').length;
    const totalAbsent = records.filter(r => r.status === 'absent').length;
    const totalLeave = records.filter(r => r.status === 'leave').length;
    const total = records.length;

    return {
      totalPresent,
      totalAbsent,
      totalLeave,
      percentage: total > 0 ? Math.round((totalPresent / total) * 100) : 0,
    };
  }

  async getCalendarEvents(startDate?: Date, endDate?: Date): Promise<CalendarEvent[]> {
    const conditions = [];

    if (startDate) {
      conditions.push(gte(calendarEvents.date, startDate));
    }
    if (endDate) {
      conditions.push(lte(calendarEvents.date, endDate));
    }

    if (conditions.length > 0) {
      return await db.select().from(calendarEvents)
        .where(and(...conditions))
        .orderBy(calendarEvents.date);
    }

    return await db.select().from(calendarEvents).orderBy(calendarEvents.date);
  }

  async createCalendarEvent(insertEvent: InsertCalendarEvent): Promise<CalendarEvent> {
    const [event] = await db.insert(calendarEvents).values(insertEvent).returning();
    return event;
  }

  async updateCalendarEvent(id: string, updates: Partial<CalendarEvent>): Promise<CalendarEvent | undefined> {
    const [event] = await db.update(calendarEvents).set(updates).where(eq(calendarEvents.id, id)).returning();
    return event || undefined;
  }

  async deleteCalendarEvent(id: string): Promise<boolean> {
    const result = await db.delete(calendarEvents).where(eq(calendarEvents.id, id));
    return (result.rowCount ?? 0) > 0;
  }

  async getExamSchedule(): Promise<ExamScheduleItem[]> {
    return await db.select().from(examSchedule).orderBy(examSchedule.date);
  }

  async createExam(insertExam: InsertExam): Promise<ExamScheduleItem> {
    const [exam] = await db.insert(examSchedule).values(insertExam).returning();
    return exam;
  }

  async updateExam(id: string, updates: Partial<ExamScheduleItem>): Promise<ExamScheduleItem | undefined> {
    const [exam] = await db.update(examSchedule).set(updates).where(eq(examSchedule.id, id)).returning();
    return exam || undefined;
  }

  async deleteExam(id: string): Promise<boolean> {
    const result = await db.delete(examSchedule).where(eq(examSchedule.id, id));
    return (result.rowCount ?? 0) > 0;
  }

  async getSyllabus(): Promise<SyllabusItem[]> {
    return await db.select().from(syllabus).orderBy(syllabus.dueDate, syllabus.subject);
  }

  async createSyllabusItem(insertItem: InsertSyllabus): Promise<SyllabusItem> {
    const [item] = await db.insert(syllabus).values(insertItem).returning();
    return item;
  }

  async updateSyllabusItem(id: string, updates: Partial<SyllabusItem>): Promise<SyllabusItem | undefined> {
    const [item] = await db.update(syllabus).set(updates).where(eq(syllabus.id, id)).returning();
    return item || undefined;
  }

  async deleteSyllabusItem(id: string): Promise<boolean> {
    const result = await db.delete(syllabus).where(eq(syllabus.id, id));
    return (result.rowCount ?? 0) > 0;
  }

  async getChatMessages(room: string, limit: number = 50): Promise<ChatMessage[]> {
    return await db.select().from(chatMessages)
      .where(eq(chatMessages.room, room))
      .orderBy(desc(chatMessages.createdAt))
      .limit(limit);
  }

  async createChatMessage(insertMessage: InsertChatMessage): Promise<ChatMessage> {
    const [message] = await db.insert(chatMessages).values(insertMessage).returning();
    return message;
  }
}

export const storage = new DatabaseStorage();

